class Operators {

  and(params) {
    return params[0] && params[1];
  }

  between(params) {
    let start = parseInt(params[0][0]);
    let end = parseInt(params[0][1]);
    let now = params[1];

    if (end < start) {
      now = (now < start) ? now += 2400 : now;
      end += 2400;
    }

    return (now > start && now < end);
  }

  equals(params) {
    return String(params[0]) === '*' || String(params[0]) === String(params[1]);
  }

  greaterThan(params) {
    return params[1] > params[0];
  }

  in(params) {
    return params[0].indexOf(params[1]) > -1;
  }

  lessThan(params) {
    return params[1] < params[0];
  }

  not(params) {
    return !params[0];
  }

  or(params) {
    return params[0] || params[1];
  }

}

export default Operators;

import Operators from './Operators';

/**
* The Policy Decision Point (PDP) decides if a message is to be authorised by checking a set of
* policies. The resource to be verified is specified in the first word of the 'condition' field of
* a Policy object. The implementation that verifies if the message is compliant with a policy is
* specified in a hashtable to allow dynamic definition of the implementation, providing
* extensibility to the Policy Engine functionalities.
*/
class PDP {

  constructor(context) {
    this.context = context;
    this.operators = new Operators();
  }

  evaluatePolicies(message, isIncomingMessage) {
    let policies = this.context.getPolicies(message, isIncomingMessage);
    let result = 'Not Applicable';
    if (policies !== undefined) {
      result = this.evaluatePolicy(message, policies.serviceProviderPolicy, isIncomingMessage);
      if (result || result === 'Not Applicable') {
        let userResult = this.evaluatePolicy(message, policies.userPolicy, isIncomingMessage);
        if (userResult !== 'Not Applicable') {
          result = userResult;
        }
      }
    }

    return result;
  }

  evaluatePolicy(message, policy, isIncoming) {
    let result = 'Not Applicable';
    if (policy) {
      result = policy.evaluateRules(this.context, message, isIncoming);
    }

    return result;
  }

}

export default PDP;

import ActionsService from './ActionsService';
import PDP from './PDP';
import Policy from './Policy';
import {isHypertyURL} from '../utils/utils';

class PEP {

  /**
  * Creates a Policy Enforcement Point (PEP) instance
  * @param    {Object}    context
  */
  constructor(context) {
    let _this = this;

    _this.pdp = new PDP(context);
    _this.actionsService = new ActionsService(context);
    _this.context = context;
    context.pep = _this;

    //TODO should be added a trigger to verify when the loadConfigurations is successfully completed
    context.loadConfigurations();
  }

  /**
  * Adds a policy to the Policy Enforcement Point (PEP). The policy can be created by the service
  * provider or by the user.
  * @param    {String}    source
  * @param    {String}    key
  * @param    {Object}    policy
  */
  addPolicy(source, key, policy, combiningAlgorithm) {
    if (!source) throw new Error('source is not defined');
    if (!key) throw new Error('key is not defined');

    if (policy === undefined) {
      policy = new Policy(key, [], [], combiningAlgorithm);
    } else {
      if (!(policy instanceof Policy)) {
        policy = new Policy(policy.key, policy.rules, policy.actions, policy.combiningAlgorithm);
      }
    }

    switch (source) {
      case 'SERVICE_PROVIDER':
        this.context.savePolicies(source, policy, key);
        break;
      case 'USER':
        this.context.userPolicies[key] = policy;
        this.context.savePolicies(source);
        break;
      default:
        throw Error('Unknown policy source: ' + source);
    }
  }

  authorise(message) {
    console.log('[Policy.PEP Authorise] ', message);
    console.log(message);
    if (!message) throw new Error('message is not defined');
    if (!message.from) throw new Error('message.from is not defined');
    if (!message.to) throw new Error('message.to is not defined');
    if (!message.type) throw new Error('message.type is not defined');
    message.body = message.body || {};

    return new Promise((resolve, reject) => {

      message.body = message.body || {};
      let _this = this;
      let result;
      if (_this._isToVerify(message)) {
        let isIncoming = _this._isIncomingMessage(message);
        _this.context.prepareForEvaluation(message, isIncoming).then(message => {
          result = _this.pdp.evaluatePolicies(message, isIncoming);
          if (result === 'Not Applicable') {
            result = _this.context.defaultBehaviour;
            message.body.auth = false;
          }
          _this.actionsService.enforcePolicies(message, isIncoming).then(messages => {
            for (let i in messages) {
              message = messages[i];
              _this.context.prepareToForward(message, isIncoming, result).then(message => {
                if (result) {
                  message.body.auth = (message.body.auth === undefined) ? true : message.body.auth;
                  resolve(message);
                } else {
                  let errorMessage = { body: { code: 403, description: 'Blocked by policy' }, from: message.to, to: message.from, type: 'response' };
                  reject(errorMessage);
                }
              }, (error) => {
                reject(error);
              });
            }
          }, (error) => {
            reject(error);
          });
        }, (error) => {
          reject(error);
        });
      } else {
        result = _this.context.defaultBehaviour;
        if (result) {
          message.body.auth = false;
          resolve(message);
        } else {
          let errorMessage = { body: { code: 403, description: 'Blocked by policy' }, from: message.to, to: message.from, type: 'response' };
          reject(errorMessage);
        }
      }
    });
  }

  authoriseSync(message) {
    let result;
    message.body = message.body || {};
    if (this._isToVerify(message)) {
      let isIncoming = this._isIncomingMessage(message);
      message = this.context.prepareForEvaluation(message, isIncoming);
      result = this.pdp.evaluatePolicies(message, isIncoming);
      if (result === 'Not Applicable') {
        result = this.context.defaultBehaviour;
        message.body.auth = false;
      }
      this.actionsService.enforcePolicies(message, isIncoming);
      message = this.context.prepareToForward(message, isIncoming, result);
      if (result) {
        message.body.auth = (message.body.auth === undefined) ? true : message.body.auth;
        return true;
      } else {
        return false;
      }
    } else {
      result = this.context.defaultBehaviour;
      if (result) {
        message.body.auth = false;
        return true;
      } else {
        return false;
      }
    }
  }

  _isIncomingMessage(message) {

    return !this.context.isLocal(message.from);
  }

  /**
  * Identifies the messages to be verified by the Policy Engine
  * @param    {Message}   message
  * @returns  {boolean}   returns true if the message requires encryption/decryption
  *                       or if its type equals 'handshake'; false otherwise
  */
  _isToVerify(message) {
    let schemasToIgnore = ['domain', 'domain-idp', 'global', 'hyperty-runtime', 'runtime'];
    let splitFrom = (message.from).split('://');
    let fromSchema = splitFrom[0];
    let splitTo = (message.to).split('://');
    let toSchema =  splitTo[0];
    let from = message.from;
    let to = message.to;

    // Signalling messages between P2P Stubs don't have to be verified. FFS

    if (message.body && message.body.source) {
      from = message.body.source;
    }

    if (message.body && message.body.subscriber) {
      from = message.body.subscriber;
    }

    if (from.indexOf('/p2phandler/') !== -1 || from.indexOf('/p2prequester/') !== -1 || to.indexOf('/p2phandler/') !== -1 || to.indexOf('/p2prequester/') !== -1) {
      return false;
    }

    // hack to disable Identity verification for messages coming from legacy domains while solution is not implemented

    if (this.context.isInterworkingProtoStub(from)) {
      return false;
    }

    if (message.from === fromSchema || message.to === toSchema || message.type === 'read' || message.type === 'response' || (isHypertyURL(message.from) && message.type === 'delete')) {
      return false;
    } else {
      return schemasToIgnore.indexOf(fromSchema) === -1 || schemasToIgnore.indexOf(toSchema) === -1;
    }
  }

  removePolicy(source, key) {
    if (!source) throw new Error('source is not defined');
    if (source !== '*' && !key) throw new Error('key is not defined');

    switch (source) {
      case '*':
        this.context.serviceProviderPolicy = {};
        this.context.userPolicies = {};
        this.context.activeUserPolicy = undefined;
        this.context.savePolicies('USER');
        this.context.savePolicies('SERVICE_PROVIDER');
        this.context.saveActivePolicy();
        break;
      case 'SERVICE_PROVIDER':
        delete this.context.serviceProviderPolicy[key];
        this.context.savePolicies();
        break;
      case 'USER':
        delete this.context.userPolicies[key];
        if (key === this.context.activeUserPolicy) {
          this.context.activeUserPolicy = undefined;
          this.context.saveActivePolicy();
        }
        this.context.savePolicies('USER');
        break;
      default:
        throw Error('Unknown policy source: ' + source);
    }
  }

}

export default PEP;

import AllowOverrides from './combiningAlgorithms/AllowOverrides';
import BlockOverrides from './combiningAlgorithms/BlockOverrides';
import FirstApplicable from './combiningAlgorithms/FirstApplicable';
import Rule from './Rule';

class Policy {

  constructor(key, rules, actions, combiningAlgorithm) {
    if (!key) throw new Error('key is not defined');
    if (!actions) throw new Error('actions are not defined');

    this.actions = actions;
    this.key = key;
    this._setRules(rules);
    this._setCombiningAlgorithm(combiningAlgorithm);
  }

  addAction(method, param) {
    this.actions.push({ method: method, param: param });
  }

  createRule(decision, condition, scope, target, priority) {
    if (priority === undefined) {
      priority = this.getLastPriority() + 1;
    }
    let rule = new Rule(decision, condition, scope, target, priority);
    this.rules.push(rule);
  }

  deleteRule(rule) {
    let indexToRemove = this.rules.indexOf(rule);
    this.rules.splice(indexToRemove, 1);
  }

  enforceActions(context, message) {
    return new Promise((resolve, reject) => {
      let results = [];
      if (this.actions.length !== 0) {
        for (let i in this.actions) {
          let result = context.pep.actionsService[this.actions[i].method](message, this.actions[i].param);
          results.push(result);
        }
        Promise.all(results).then((messages) => {
          resolve(messages);
        }, error => {
          reject(error);
        });
      } else {
        resolve([message]);
      }
    });
  }

  evaluateRules(context, message, isIncoming) {
    let results = [];
    for (let i in this.rules) {
      results.push(this.rules[i].evaluate(context, message, isIncoming));
    }

    return this.combiningAlgorithm.combine(results);
  }

  getLastPriority() {
    let priorities = [];

    if (this.rules.length !== 0) {
      for (let i in this.rules) {
        priorities.push(this.rules[i].priority);
      }
      return Math.max.apply(Math, priorities);
    } else {
      return -1;
    }
  }

  getRuleByPriority(priority) {
    for (let i in this.rules) {
      if (String(this.rules[i].priority) === String(priority)) {
        return this.rules[i];
      }
    }
    throw Error('Rule with priority ' + priority + ' does not exist!');
  }

  _setCombiningAlgorithm(combiningAlgorithm) {
    if (!combiningAlgorithm) {
      combiningAlgorithm = 'blockOverrides';
    }
    switch (combiningAlgorithm) {
      case 'blockOverrides':
        this.combiningAlgorithm = new BlockOverrides();
        break;
      case 'allowOverrides':
        this.combiningAlgorithm = new AllowOverrides();
        break;
      case 'firstApplicable':
        this.combiningAlgorithm = new FirstApplicable();
        break;
      default:
        throw Error('Unknown algorithm: ' + combiningAlgorithm);
    }
  }

  _setRules(rules) {
    this.rules = [];

    for (let i in rules) {
      let rule = rules[i];
      if (rule.priority === undefined) {
        rule.priority = this.getLastPriority() + 1;
      }
      if (!(rule instanceof Rule)) {
        rule = new Rule(rule.decision, rule.condition, rule.scope, rule.target, rule.priority);
      }
      this.rules.push(rule);
    }
  }

  sortRules() {
    return this.rules.sort(function(a, b) {
      let x = a.priority; let y = b.priority;
      return ((x < y) ? -1 : ((x > y) ? 1 : 0));
    });
  }

}

export default Policy;

import {divideEmail, divideURL, isDataObjectURL} from '../utils/utils';

class ReThinkCtx {

  constructor() {
    this.defaultBehaviour = true;
    this.groups = {};
  }

  get scheme() {
    return this._scheme;
  }

  get date() {
    return this._date;
  }

  get domain() {
    return this._domain;
  }

  get type() {
    return this._type;
  }

  get source() {
    return this._source;
  }

  get time() {
    return this._time;
  }

  get weekday() {
    return this._weekday;
  }

  set scheme(params) {
    let from = params.message.from;
    if (isDataObjectURL(from)) {
      this._scheme = divideURL(from).type;
    } else {
      this._scheme = undefined;
    }
  }

  set date(now) {
    let date = new Date();
    let day = String(date.getDate());
    if (day.length === 1) {
      day = '0' + day;
    }
    let month = String(date.getMonth() + 1);
    if (month.length === 1) {
      month = '0' + month;
    }
    this._date = day + '/' + month + '/' + date.getFullYear();
  }

  set domain(params) {
    if (params.message.body.identity !== undefined) {
      this._domain = divideEmail(params.message.body.identity.userProfile.username).domain;
    }
  }

  set type(params) {
    let message = params.message;
    if (message.body.value !== undefined) {
      this._type = message.body.value.resourceType;
    }
  }

  set source(params) {
    if (params.message.body.identity !== undefined) {
      this._source = params.message.body.identity.userProfile.username;
    }
  }

  set time(now) {
    now = new Date();
    let minutes = String(now.getMinutes());
    if (minutes.length === 1) {
      minutes = '0' + minutes;
    }
    this._time = parseInt(String(now.getHours()) + minutes);
  }

  set weekday(now) {
    this._weekday = String(new Date().getDay());
  }

}

export default ReThinkCtx;

import AdvancedCondition from './conditions/AdvancedCondition';
import Condition from './conditions/Condition';
import {getUserEmailFromURL, isDataObjectURL, removePathFromURL} from '../utils/utils';
import SubscriptionCondition from './conditions/AdvancedCondition';

class Rule {

  constructor(decision, condition, scope, target, priority) {
    this.decision = decision;
    this.setCondition(condition);
    this.priority = priority;
    this.scope = scope;
    this.target = target;
  }

  setCondition(condition) {
    if (!(condition instanceof Condition || condition instanceof SubscriptionCondition || condition instanceof AdvancedCondition)) {
      let attribute = condition.attribute;
      switch (attribute) {
        case 'subscription':
          this.condition = new SubscriptionCondition(condition.attribute, condition.operator, condition.params);
          break;
        case undefined:
          this.condition = new AdvancedCondition(condition);
          break;
        default:
          this.condition = new Condition(condition.attribute, condition.operator, condition.params);
      }
    } else {
      this.condition = condition;
    }
  }

  evaluate(context, message, isIncoming) {
    let field = (isIncoming) ? message.to : message.from;
    let hypertyName;
    switch (this.scope) {
      case 'global':
        break;

      case 'hyperty':
        if (isDataObjectURL(field)) {
          let reporter = context.runtimeRegistry.getReporterURLSynchonous(removePathFromURL(field));
          if (reporter !== undefined) {
            hypertyName = context.runtimeRegistry.getHypertyName(reporter);
          }
        } else {
          if (field.split('://')[0] === 'hyperty') {
            hypertyName = context.runtimeRegistry.getHypertyName(removePathFromURL(field));
          }
        }
        if (hypertyName === this.target) {
          break;
        }

        return 'Not Applicable';

      case 'identity':
        let owner;

        if (isDataObjectURL(field)) {
          let reporter = context.runtimeRegistry.getReporterURLSynchonous(removePathFromURL(field));
          owner = context.runtimeRegistry.getHypertyOwner(reporter);
        } else {
          if (field.split('://')[0] === 'hyperty') {
            owner = context.runtimeRegistry.getHypertyOwner(removePathFromURL(field));
          }
        }
        if (owner !== undefined) {
          owner = getUserEmailFromURL(owner);
        }
        if (owner === this.target) {
          break;
        }

        return 'Not Applicable';
    }

    if (this.condition.isApplicable(context, message, this.scope, this.target)) {
      return this.decision;
    } else {
      return 'Not Applicable';
    }
  }
}

export default Rule;

