*/
class BlockOverrides {

  /**
  * Given an array of individual authorisation decisions, prioritises a negative one.
  * @param    {boolean[]}   decisions
  * @returns  {boolean}
  */
  combine(decisions) {
    if (decisions.indexOf(false) !== -1) {
      return false;
    } else {
      if (decisions.indexOf(true) !== -1) {
        return true;
      } else {
        return 'Not Applicable';
      }
    }
  }

}

