import chai from 'chai';
import MessageBus from '../src/bus/MessageBus';

let expect = chai.expect;

describe('MessageBus', function() {
  it('sending message', function(done) {
    let msgResult;

    let mockRegistry = {
      resolve(url) {
        return new Promise((resolve) => {
          //resolve to the same URL
          resolve(url);
        });
      }
    };

    let msgBus = new MessageBus(mockRegistry);
    msgBus.addListener('hyper-2', (msg) => {
      msgResult = msg;
    });

    msgBus.postMessage({
      from: 'hyper-1', to: 'hyper-2',
      body: {value: 'x'}
    });

    setTimeout(() => {
      expect(msgResult).to.eql({
        id: 1, from: 'hyper-1', to: 'hyper-2',
        body: {value: 'x'}
      });

      done();
    });
  });

  it('pipeline msg change', function(done) {
    let mBus = new MessageBus();
    mBus.pipeline.handlers = [
      function(ctx) {
        ctx.msg.token = '12345678';
        ctx.next();
      }
    ];

    mBus.addListener('hyper-2', (msg) => {
      expect(msg).to.eql({ id: 1, type: 'ping', token: '12345678', from: 'hyper-1', to: 'hyper-2' });
      done();
    });

    mBus.postMessage({ type: 'ping', from: 'hyper-1', to: 'hyper-2' });
  });

  it('sending using external system', function(done) {
    let msgResult;

    let mockRegistry = {
      resolve() {
        return new Promise((resolve) => {
          //resolve to default
          resolve('protostub');
        });
      }
    };

    let msgBus = new MessageBus(mockRegistry);
    msgBus.addListener('protostub', (msg) => {
      msgResult = msg;
    });

    msgBus.postMessage({
      from: 'hyper-1', to: 'hyper-2/other',
      body: {value: 'x'}
    });

    setTimeout(() => {
      expect(msgResult).to.eql({
        id: 1, from: 'hyper-1', to: 'hyper-2/other',
        body: {value: 'x'}
      });

      done();
    });

  });

  it('publish unique messages', function(done) {
    let result = { obj1: 0, obj2: 0 };

    let msgBus = new MessageBus();
    msgBus._onPostMessage = (msg) => {
      console.log(msg);
      result[msg.to]++;
    };

    msgBus.addPublish('obj1');
    msgBus.addPublish('obj1'); //repeated route ignored
    msgBus.addPublish('obj2');

    setTimeout(() => {
      msgBus.postMessage({ from: 'x', to: 'obj1' });
      msgBus.postMessage({ from: 'x', to: 'obj2' });
      msgBus.postMessage({ from: 'x', to: 'obj2' });

      setTimeout(() => {
        expect(result).to.eql({ obj1: 1, obj2: 2 });
        done();
      });
    });
  });
});

import chai from 'chai';
import MiniBus from '../src/bus/MiniBus';

let expect = chai.expect;

describe('MiniBus', function() {
  it('simple sending message', function(done) {
    let mBus = new MiniBus();
    mBus._onPostMessage = (msg) => {
      expect(msg).to.eql({
        id: 1, type: 'test', from: 'hyper-1', to: 'hyper-2',
        body: {value: 'x'}
      });

      done();
    };

    mBus.postMessage({
      type: 'test', from: 'hyper-1', to: 'hyper-2',
      body: {value: 'x'}
    });
  });

  it('send with external response', function(done) {
    this.timeout(4000);
    let msgResult = {};

    let mBus = new MiniBus();
    mBus.addListener('hyper-1', (msg) => {
      msgResult = msg;
    });

    mBus._onPostMessage = (msg) => {
      expect(msg).to.eql({
        id: 1, type: 'test', from: 'hyper-1', to: 'hyper-2',
        body: {value: 'x'}
      });

      mBus._onMessage({
        id: 1, type: 'response', from: 'hyper-2', to: 'hyper-1',
        body: {value: 'y'}
      });
    };

    mBus.postMessage({
      type: 'test', from: 'hyper-1', to: 'hyper-2',
      body: {value: 'x'}
    }, (response) => {
      expect(response).to.eql({
        id: 1, type: 'response', from: 'hyper-2', to: 'hyper-1',
        body: {value: 'y'}
      });

      setTimeout(() => {
        //expect not to enter in the hyper-1 listener
        expect(msgResult).to.be.empty;
        done();
      });
    });
  });

  it('send and publish', function(done) {
    let mBus = new MiniBus();
    let msgResult = {};

    let defaultListener = false;
    let hyper2Listener = false;
    let objListener = false;

    mBus.addListener('*', (msg) => {
      msgResult = msg;
      defaultListener = true;
    });

    mBus.addListener('hyperty://hyper-2', (msg) => {
      msgResult = msg;
      hyper2Listener = true;
    });

    mBus.addListener('resource://fake-url', (msg) => {
      msgResult = msg;
      objListener = true;
    });

    //simulate message from MessageBus core
    mBus._onMessage({
      id: 1, type: 'send', from: 'hyperty://hyper-1', to: 'hyperty://hyper-2',
      body: {value: 'x'}
    });

    setTimeout(() => {
      //should be only received in the hyperty listener
      expect(defaultListener).to.eql(false);
      expect(hyper2Listener).to.eql(true);
      expect(msgResult).to.eql({
        id: 1, type: 'send', from: 'hyperty://hyper-1', to: 'hyperty://hyper-2',
        body: {value: 'x'}
      });

      //simulate message from MessageBus core
      mBus._onMessage({
        id: 2, type: 'publish', from: 'hyperty://hyper-1', to: 'resource://fake-url',
        body: {value: 'x'}
      });

      setTimeout(() => {
        //should be received in default and object listener
        expect(defaultListener).to.eql(true);
        expect(objListener).to.eql(true);
        expect(msgResult).to.eql({
          id: 2, type: 'publish', from: 'hyperty://hyper-1', to: 'resource://fake-url',
          body: {value: 'x'}
        });
        done();
      });
    });
  });
});

